// AUD.js
import React, { useState } from 'react';
import InputWithMoreButton from './InputWithMoreButton';

function AUD() {
  const [values, setValues] = useState(['']); // Almacena los valores en un arreglo

  const handleAmountChange = (event, index) => {
    const newValue = event.target.value;
    const newValues = [...values];
    newValues[index] = newValue;
    setValues(newValues);
  };

  const addValue = () => {
    setValues([...values, '']); // Agrega un nuevo valor vacío
  };

  return (
    <div className="w-full bg-gray-100 p-4 flex flex-col items-center"> {/* Flex container */}
      <p className="mb-2 text-2xl font-semibold" style={{ fontFamily: 'Inter, sans-serif' }}>
        Ingrese los montos en AUD:
      </p>
      {values.map((value, index) => (
        <InputWithMoreButton
          key={index}
          value={value}
          onChange={(event) => handleAmountChange(event, index)}
          addValue={addValue}
          isLast={index === values.length - 1} // Determina si es el último input
        />
      ))}
    </div>
  );
}

export default AUD;
