// src/components/USD.js
import React, { useState, useEffect } from 'react';

function USD({ amountInAUD }) {
  const [amountInUSD, setAmountInUSD] = useState(0);
  const API_KEY = '3add9570361a154fa2b797b30572fda7'; // Tu API Key de Fixer
  const BASE_URL = `http://data.fixer.io/api/latest?access_key=${API_KEY}&symbols=USD`;

  useEffect(() => {
    // Realizar la solicitud a la API de Fixer para obtener las tasas de conversión
    fetch(BASE_URL)
      .then(response => response.json())
      .then(data => {
        const exchangeRate = data.rates.USD;
        const convertedAmount = (amountInAUD * exchangeRate).toFixed(2);
        setAmountInUSD(convertedAmount);
      })
      .catch(error => {
        console.error('Error fetching data from Fixer API:', error);
      });
  }, [amountInAUD]);

  return (
    <div className="w-full bg-gray-100 p-4 mt-4">
      <p className="mb-2 text-2xl font-semibold" style={{ fontFamily: 'Inter, sans-serif', fontWeight: 900 }}>
        El valor en USD es de &gt; 
      </p>
      <p className="text-2xl font-semibold" style={{ fontFamily: 'Inter, sans-serif', fontWeight: 900 }}>
        {amountInUSD} USD
      </p>
    </div>
  );
}

export default USD;
