// src/components/USD.js
import React from 'react';

function USD({ values }) {
  if (!Array.isArray(values) || values.length === 0) {
    return <p>No hay valores para convertir a USD.</p>;
  }

  const customExchangeRate = 0.66;
  const totalInUSD = values.reduce((total, amountInAUD) => {
    const amountInUSD = parseFloat(amountInAUD) * customExchangeRate;
    return total + amountInUSD;
  }, 0).toFixed(2);

  return (
    <div className="w-full bg-gray-100 p-4 mt-4 text-center">
      <p className="mb-2 text-large font-semibold">
        El valor total en USD es:
      </p>
      <p className="text-3xl font-semibold">
        {totalInUSD} USD
      </p>
    </div>
  );
}

export default USD;
