// src/components/USD.js
import React, { useEffect, useState } from 'react';

function USD({ values }) {
  const [totalInUSD, setTotalInUSD] = useState(0.00);

  const customExchangeRate = 0.66; // Tasa de conversión manual

  useEffect(() => {
    // Calcular el valor total en USD aquí cuando los valores cambien
    const total = values.reduce((acc, value) => {
      const parsedValue = parseFloat(value);
      return isNaN(parsedValue) ? acc : acc + parsedValue;
    }, 0) * customExchangeRate;

    setTotalInUSD(total.toFixed(2));
  }, [values]);

  return (
    <div className="w-full bg-gray-100 p-4 mt-4 text-center">
      <p className="mb-2 text-2xl font-semibold">El valor total en USD es:</p>
      <p className="text-3xl font-semibold">{totalInUSD} USD</p>
    </div>
  );
}

export default USD;
