// src/components/AUD.js
import React, { useState } from 'react';

function AUD() {
  const [amount, setAmount] = useState('');

  const handleAmountChange = (event) => {
    const newValue = event.target.value;

    // Validación usando una expresión regular para permitir números enteros y decimales
    if (newValue === '' || /^[0-9]*\.?[0-9]*$/.test(newValue)) {
      setAmount(newValue);
    }
  };

  return (
    <div className="w-full bg-gray-100 p-4">
      <p className="mb-2 text-2xl font-semibold" style={{ fontFamily: 'Inter, sans-serif' }}>
        Ingrese el monto en AUD:
      </p>
      <input
        type="text"
        id="amount"
        placeholder="Campo Numerito"
        value={amount}
        onChange={handleAmountChange}
        className="w-full px-3 py-2 border rounded-md"
        pattern="[0-9]*\.?[0-9]*" // Expresión regular para permitir números enteros y decimales
      />
    </div>
  );
}

export default AUD;
